function irecco_fonts_url()
{
	$fonts_url = '';

	/* Translators: If there are characters in your language that are not
	* supported by Ubuntu, translate this to 'off'. Do not translate
	* into your own language.
	*/
	$muli = esc_html_x('on', 'Ubuntu font: on or off', 'irecco');

	/* Translators: If there are characters in your language that are not
	* supported by Muli, translate this to 'off'. Do not translate
	* into your own language.
	*/
	$nunito = esc_html_x('off', 'Nunito font: on or off', 'irecco');

	if ('off' !== $nunito || 'off' !== $muli) {
		$font_families = [];

		if ('off' !== $nunito) $font_families[] = 'Nunito:400,600,700';
		if ('off' !== $muli)   $font_families[] = 'Muli:400,500,600,700,800';

		$query_args = [
			'family' => urlencode(implode('|', $font_families)),
			'subset' => urlencode('latin,latin-ext'),
		];

		$fonts_url = add_query_arg($query_args, 'https://fonts.googleapis.com/css');
	}

	return esc_url_raw($fonts_url);
}

function editor_google_fonts()
{
	wp_enqueue_style('editor-google-fonts', irecco_fonts_url(), array(), '1.0.0');
}

add_action('admin_enqueue_scripts', 'editor_google_fonts');

if (!class_exists('iRecco_Core_Public')) {

	function irecco_default_fonts()
	{
		wp_enqueue_style('irecco-default-font', irecco_fonts_url(), array(), '1.0.0');
	}

	add_action('wp_enqueue_scripts', 'irecco_default_fonts');
}